import tornado.webimport tornado.wsgiimport osimport tool.configimport loggingfrom basehandler import BaseHandlerfrom login import LoginPagefrom login import LogoutPageclass MainHandler(BaseHandler):    def get(self):        self.render("home.html")class DBinit(tornado.web.RequestHandler):    def get(self):        tool.config.debug_init()      settings = {    'template_path'  : os.path.join(os.path.dirname(__file__), 'templates'),    'cookie_secret'  : tool.config.APP_Key.get('cookie_secret'),    "login_url"      : "/login",    'debug'          : False,}tornado_app = tornado.web.Application([    (r'/login',  LoginPage),    (r'/logout', LogoutPage),    (r'/dbinit', DBinit),    (r"/",       MainHandler),],**settings)app = tornado.wsgi.WSGIAdapter(tornado_app)