#coding=utf-8import tornado.webimport tornado.wsgiimport osimport tool.configimport loggingfrom basehandler import BaseHandlerfrom login import LoginPagefrom login import LogoutPagefrom home import HomePageclass MainHandler(BaseHandler):    def get(self):        self.render("front.html")class RemoveSlash(BaseHandler):    def get(self,path):        self.redirect(path)    def post(self,path):        self.redirect(path)class DBinit(tornado.web.RequestHandler):    def get(self):        tool.config.debug_init()settings = {    'template_path'  : os.path.join(os.path.dirname(__file__), 'templates'),    # 'cookie_secret'  : tool.config.APP_Key.get('cookie_secret'),    'cookie_secret'  : 'GG',    "login_url"      : '/login',    'debug'          : False,}tornado_app = tornado.web.Application([    (r'(?:(.+)/)',   RemoveSlash),    (r'/login/?',    LoginPage),    (r'/logout/?',   LogoutPage),    (r'/home/?',     HomePage),    (r'/dbinit/?',   DBinit),    (r"/",           MainHandler),],**settings)app = tornado.wsgi.WSGIAdapter(tornado_app)